'use strict';

 var fb = require('../../waterlock-facebook-auth').fb;

/**
 * Login action
 */
module.exports = function(req, res){
  var origin = req.query.originUrl;
  Object.keys(req.query).forEach(function(key){
    if(key!='type' && key != 'originUrl'){
      origin += '&' + key + "=" + req.query[key];
    }
  });
  res.redirect(fb.getLoginDialogURI(null, origin));
};
